    xml += `V10QR-${i+1}\n`;
    xml += `Viktoria 10-QR â€“ ${c.color}\n`;
    xml += `${imageBase}${c.file}\n`;
    xml += `5.00 AZN\n`;
    xml += `in stock\n`;
    xml += `\n`;

  });

  xml += `\n`;

  const blob = new Blob([xml], { type: "text/xml" });
  const url = URL.createObjectURL(blob);

  const a = document.createElement("a");
  a.href = url;
  a.download = "feed.xml";
  a.click();

}

